import type { Node, TemplateAPI } from "@livereader/graphly-d3";

export type Schema = {
  title: string;
  daytype: "day" | "night" | "dawn" | "grey" | "blue" | "custom";
  moon?: boolean;
  temp: number;
  size: number;
  weathertype: "clear" | "cloudy" | "rainy" | "snowy" | "windy" | "foggy";
  ringColor: {
    outer: string;
    middle: string;
    inner: string;
  };
  timeColor: string;
  time: string;
};

const schema = {
  type: "object",
  properties: {
    title: { type: "string" },
    daytype: {
      type: "string",
      enum: ["day", "night", "dawn", "grey", "blue", "custom"],
    },
    moon: { type: "boolean" },
    temp: { type: "number" },
    size: { type: "number" },
    weathertype: {
      type: "string",
      enum: ["clear", "cloudy", "rainy", "snowy", "windy", "foggy"],
    },
    ringColor: {
      type: "object",
      properties: {
        outer: { type: "string" },
        middle: { type: "string" },
        inner: { type: "string" },
      },
    },
    timeColor: { type: "string" },
    time: { type: "string" },
  },
  required: ["title", "daytype", "temp", "weathertype", "time"],
};

